<script>
export default {
  name: 'HomeCover',
  data () {
    return {
      // (1921 / 1388).toFixed(2) = 1.38
      clickablePersonsAspectRatio: 1.38,
      ww: document.documentElement.clientWidth,
      wh: document.documentElement.clientHeight,
      isEnteringReportCover: false
    }
  },
  created () {
    window.addEventListener('resize', this.alterWindowSize)
  },
  computed: {
    clickablePersonsSize () {
      const windowAspectRatio = this.ww / this.wh
      return this.clickablePersonsAspectRatio > windowAspectRatio ? { width: `${1921 * (this.wh / 1388)}px` } : { height: `${1388 * (this.ww / 1921)}px` }
    }
  },
  methods: {
    alterWindowSize () {
      this.ww = document.documentElement.clientWidth
      this.wh = document.documentElement.clientHeight
    },
    showPerson (evt) {
      const self = evt.currentTarget
      const idx = self.dataset.person
      // const person = `#person${idx}`
      this.$root.currentPerson = document.getElementById(`person${idx}`)
      TweenLite.to(this.$root.currentPerson, 0.4, {
        css: {
          opacity: 1
        },
        ease: Power3.easeOut
      })
    },
    hidePerson (evt) {
      if (this.isEnteringReportCover) return
      TweenLite.to(this.$root.currentPerson, 0.4, {
        css: {
          opacity: 0
        },
        ease: Power3.easeOut
      })
    },
    showReportCover (evt) {
      const self = evt.currentTarget
      const idx = self.dataset.person
      this.$root.currentReport = document.getElementById(`report${idx}`)
      this.isEnteringReportCover = true
      this.$root.inHome = false
      TweenLite.set(this.$root.currentReport, {
        css: {
          position: 'absolute',
          height: '100vh',
          opacity: 0,
          y: '',
          className: '+=report--current'
        }
      })
      TweenLite.to('#home-cover', 0.8, {
        css: {
          opacity: 0
        },
        ease: Power3.easeIn,
        onComplete: () => {
          TweenLite.to(this.$root.currentReport, 0.8, {
            css: {
              opacity: 1
            },
            ease: Power3.easeIn
          })
          this.$root.currentPerson.style.opacity = 0
          this.$root.currentPerson = null
          this.isEnteringReportCover = false
        }
      })
    }
  }
}
</script>

<style lang="scss">
#home-cover {
  position: absolute;
  top: 0;
  width: 100%;
  height: 100vh;
  overflow: hidden;
}
.home-cover {
  &__mrt {
    height: 100%;
    background-image: url(../assets/img/cover/lap/home_mrt.jpg);
  }
  &__clickable-persons {
    position: absolute;
    top: 0;
    left: 50%;
    transform: translateX(-50%);
    z-index: 9;
    opacity: 0;
    & g {
      cursor: pointer;
    }
  }
  &__person {
    opacity: 0;
  }
}
#person1 {
  background-image: url(../assets/img/cover/lap/home_person1.png);
}
#person2 {
  background-image: url(../assets/img/cover/lap/home_person2.png);
}
#person3 {
  background-image: url(../assets/img/cover/lap/home_person3.png);
}
#person4 {
  background-image: url(../assets/img/cover/lap/home_person4.png);
}
#person5 {
  background-image: url(../assets/img/cover/lap/home_person5.png);
}
</style>
